import star from './images/icon-star.svg'
import thanks from './images/illustration-thank-you.svg'

function App() {
  const Btn = ({ num }) => {
    return <button className="btnNum text-gray-100 bg-gray-700 w-10 h-10 pt-1 rounded-full">{num}</button>
  }

  return (
    <div className="wrapper p-4">
      <img className="bg-gray-600 p-1 rounded-full" src={star} alt='' />
      <h2 className="py-5">How did we do?</h2>
      <p>Please let us know how we did with your support request. All feedback is appreciated to help us improve our offering!</p>
      <ul className="grid grid-cols-5 mb-8 pt-5 pl-3 gap-5">
        <li><Btn num={1} /></li>
        <li><Btn num={2} /></li>
        <li><Btn num={3} /></li>
        <li><Btn num={4} /></li>
        <li><Btn num={5} /></li>
      </ul>
      <div className="text-center">
        <button className="btn-submit uppercase w-80 py-1.5">submit</button>
      </div>
    </div>
  );
}

const ThankYou = () => {
  return (
    <div className="wrapper">
      <img src={thanks} />
    </div>
  )
}

export default App;
